import React, { useState } from 'react'
import { homeWorkReducer } from './bll/homeWorkReducer'
import s from './HW8.module.css'
import s2 from '../../s1-main/App.module.css'
import SuperButton from '../hw04/common/c2-SuperButton/SuperButton'
import User from './User'

export type UserType = {
    _id: number
    name: string
    age: number
}

const initialPeople: UserType[] = [
    {_id: 0, name: 'Кот', age: 20},
    {_id: 1, name: 'Александр', age: 66},
    {_id: 2, name: 'Коля', age: 32},
    {_id: 3, name: 'Виктор', age: 44},
    {_id: 4, name: 'Дмитрий', age: 40},
    {_id: 5, name: 'Ирина', age: 55},
]

const HW8 = () => {
    const [people, setPeople] = useState<UserType[]>(initialPeople)
    const [currentSort, setCurrentSort] = useState<'up' | 'down' | '18' | ''>('')


    const finalPeople = people.map(user => <User key={user._id + currentSort} user={user} />)




    const sortUp = () => {
        setPeople([]) // очистка
        setTimeout(() => {
            setPeople(homeWorkReducer(initialPeople, { type: 'sort', payload: 'up' }))
            setCurrentSort('up')
        }, 0)
    }
    const sortDown = () => {
        setPeople(homeWorkReducer(initialPeople, { type: 'sort', payload: 'down' }))
        setCurrentSort('down')
    }

    const check18 = () => {
        setPeople(homeWorkReducer(initialPeople, { type: 'check', payload: 18 }))
        setCurrentSort('18')
    }

    return (
        <div id={'hw8'}>
            <div className={s2.hwTitle}>Homework #8</div>
            <div className={s2.hw}>
                <div className={s.container}>
                    <div className={s.buttonsContainer}>
                        <SuperButton
                            id={'hw8-button-up'}
                            onClick={sortUp}
                            xType={currentSort === 'up' ? '' : 'secondary'}
                        >
                            Sort up
                        </SuperButton>
                        <SuperButton
                            id={'hw8-button-down'}
                            onClick={sortDown}
                            xType={currentSort === 'down' ? '' : 'secondary'}
                        >
                            Sort down
                        </SuperButton>
                        <SuperButton
                            id={'hw8-button-18'}
                            onClick={check18}
                            xType={currentSort === '18' ? '' : 'secondary'}
                        >
                            Check 18+
                        </SuperButton>
                    </div>

                    <table id={'hw8-users'} className={s.users}>
                        <thead className={s.thead}>
                        <tr>
                            <th className={s.nameCol}>Name</th>
                            <th className={s.ageCol}>Age</th>
                        </tr>
                        </thead>
                        <tbody>{finalPeople}</tbody>
                    </table>
                </div>
            </div>
        </div>
    )
}

export default HW8
